    array: [u8]
) -> Field {
    let mut addr: Field = 0;

    for i in 0..20 {
        // only take the last 20 bytes of the hash
        addr = (addr * 256) + (array[i] as Field);
    }

    addr
}

fn u8_to_eth_address(
    array: [u8]
) -> Field {
    u8_to_u160(array)
}

unconstrained
